/** Εξάγει μια υπεύθυνη δήλωση δικαιούχου.
 * @param callable $function Κλήση που επεξεργάζεται τα δεδομένα */
function statement_common($function) {
	global $data;
	foreach($data['Δικαιούχοι'] as $per_contractor) {
		$v = $per_contractor['Δικαιούχος'];
		if ($v['Τύπος'] == 'Ιδιωτικός Τομέας') {
			$v['Ημερομηνία Έκδοσης'] =
					strftime('%d %b %y', get_newer_timestamp($per_contractor['Τιμολόγια']));
			if ($function !== 'statement_disqualification') $function($v);
			elseif (isset($per_contractor['Σύμβαση']) && !isset($per_contractor['Σύμβαση']['Διαγωνισμός']))
				$function($v, $per_contractor['Σύμβαση']);
		}
	}
}

/** Εξάγει μια υπεύθυνη δήλωση δικαιούχου.
 * @param array $contractor Τα δεδομένα της υπεύθυνης δήλωσης */
function statement_contractor($contractor) {
	global $data;
	$contractor['Προς'] = rtf(inflectPhrase($data['Μονάδα Πλήρες'], 2));
	if (!isset($contractor['Ονοματεπώνυμο'])) $contractor['Ονοματεπώνυμο'] = $contractor['Επωνυμία'];
	if (!isset($contractor['Διεύθυνση Κατοικίας'])) $contractor['Διεύθυνση Κατοικίας'] = $contractor['Διεύθυνση'];
	statement($contractor);
}

/** Εξάγει μια υπεύθυνη δήλωση δικαιούχου που γνωστοποιεί το IBAN.
 * @param array $contractor Τα δεδομένα ενός δικαιούχου */
function statement_IBAN($contractor) {
	$contractor['Δήλωση'] = 'Το χρηματικό ποσό που δικαιούμαι να εισπράξω σαν «{\b '
			. rtf($contractor['Επωνυμία']) . '}», ΑΦΜ: ' . rtf($contractor['ΑΦΜ']) . ', τηλέφωνο: '
			. rtf($contractor['Τηλέφωνο']) . ', να κατατεθεί στο λογαριασμό IBAN {\b '
			. iban($contractor['ΙΒΑΝ']) . '} ({\b ' . bank($contractor['ΙΒΑΝ'])
			. '}).\par Επισυνάπτεται φωτοτυπία του βιβλιαρίου καταθέσεων.';
	statement_contractor($contractor);
}

/** Εξάγει μια υπεύθυνη δήλωση δικαιούχου ότι δε χρησιμοποιεί απόστρατο αντιπρόσωπο.
 * @param array $contractor Τα δεδομένα ενός δικαιούχου */
function statement_representative($contractor) {
	$contractor['Δήλωση'] = 'Ως νόμιμος εκπρόσωπος της επιχείρησης με επωνυμία «' . rtf($contractor['Επωνυμία']) . '», δε θα χρησιμοποιήσω σαν άμεσο ή έμμεσο αντιπρόσωπο, μόνιμο ή σε εφεδρεία Αξιωματικό των τριών κλάδων των Ενόπλων Δυνάμεων, εφόσον δεν έχει παρέλθει πενταετία από τον χρόνο αποστρατείας τους, σύμφωνα με την παράγραφο 12 του άρθρου 66 του ΝΔ 1400/1973 (Α\' 114), όπως τροποποιήθηκε και ισχύει.';
	statement_contractor($contractor);
}

/** Εξάγει μια υπεύθυνη δήλωση δικαιούχου ότι δε συντρέχουν λόγοι αποκλεισμού κατά την απευθείας ανάθεση.
 * @param array $contractor Τα δεδομένα ενός δικαιούχου
 * @param array $contract Τα δεδομένα της σύμβασης με το δικαιούχο */
function statement_disqualification($contractor, $contract) {
	global $data;
	$contractor['Δήλωση'] = 'Ως νόμιμος εκπρόσωπος της επιχείρησης με επωνυμία «' . rtf($contractor['Επωνυμία']) . '» η οποία εδρεύει στη διεύθυνση ' . rtf($contractor['Διεύθυνση']) . ' με ΑΦΜ ' . rtf($contractor['ΑΦΜ']) . ' και ΔΟΥ ' . rtf($contractor['ΔΟΥ']) . ':\par' . PHP_EOL .
		'1.\tab Αποδέχομαι τους όρους της πρόσκλησης υποβολής προσφορών ' . (isset($contract['Πρόσκληση Υποβολής Προσφορών']) ? $contract['Πρόσκληση Υποβολής Προσφορών']['Ταυτότητα'] : 'Φ............................................') . '.\par' . PHP_EOL .
		'2.\tab Δεν έχω καταδικαστεί με αμετάκλητη απόφαση για κάποιο από τα αδικήματα που συνιστούν υποχρεωτικούς λόγους αποκλεισμού της παραγράφου 1 του άρθρου 73 του Ν.4412/2016 (Α\' 147), όπως αυτός τροποποιήθηκε με τον Ν4488/2017 (Α\' 137). Ούτε εμπίπτω σε κάποια κατηγορία δυνητικού αποκλεισμού που καθίσταται υποχρεωτικός από την παραπάνω πρόσκληση υποβολής προσφορών.\par' . PHP_EOL .
		'3.\tab Αναλαμβάνω την υποχρέωση προσκόμισης των παρακάτω πιστοποιητικών για την απόδειξη μη συνδρομής των προαναφερθέντων λόγων αποκλεισμού, κατά το χρόνο που θα μου ζητηθεί από ' . rtf(article(gender($data['Μονάδα Πλήρες']), 2) . ' ' . inflectPhrase($data['Μονάδα Πλήρες'], 2)) . ' και πριν την έκδοση απόφασης απευθείας ανάθεσης. Ειδικότερα:\par' . PHP_EOL .
		'\tab α.\tab Απόσπασμα ποινικού μητρώου.\par' . PHP_EOL .
		'\tab β.\tab Πιστοποιητικό φορολογικής ενημερότητας.\par' . PHP_EOL .
		'\tab γ.\tab Πιστοποιητικό ασφαλιστικής ενημερότητας.\par' . PHP_EOL .
		'\tab δ.\tab Υπεύθυνη δήλωση αναφορικά με τους οργανισμούς κύριας και επικουρικής ασφάλισης στους οποίους οφείλω να καταβάλω εισφορές.\par' . PHP_EOL .
		'\tab ε.\tab Υπεύθυνη δήλωση περί μη έκδοσης δικαστικής ή διοικητικής απόφασης με τελεσίδικη και δεσμευτική ισχύ, για την αθέτηση των υποχρεώσεών μου όσον αφορά στην καταβολή φόρων ή εισφορών κοινωνικής ασφάλισης.';
	statement_contractor($contractor);
}


/** Εξάγει την υπεύθυνη δήλωση της αντίστοιχης φόρμας του γραφικού περιβάλλοντος. */
function statement_gui() {
	global $data;
	$data['Δήλωση'] = str_replace(array("\r\n", "\r", "\n"), '\par ', str_replace("\t", '\tab ', rtf($data['Δήλωση'])));
	statement($data);
}